      ctx.fill();

      // Update moon rotation
      gameState.moonRotation += 0.002 + score * 0.0001;

      // Score display (exactly like index.html)
      ctx.fillStyle = '#ffff96';
      ctx.textAlign = 'left';
      ctx.textBaseline = 'top';
      ctx.font = 'bold 20px Arial';
      ctx.fillText(`Score: ${score}`, -canvas.width / 2 + 20, -canvas.height / 2 + 20);

      // Check for brownie points (every 50 points)
      let currentBrowniePoints = Math.floor(score / 50);
      if (currentBrowniePoints > browniePoints && score > 0) {
        setBrowniePoints(currentBrowniePoints);
        setShowBrownie(true);
        setBrownieTimer(180); // Show for 3 seconds (60fps * 3)
      }

      // Display brownie point message
      if (showBrownie && brownieTimer > 0) {
        setBrownieTimer(prev => prev - 1);

        // Fade out effect
        let alpha = brownieTimer < 60 ? brownieTimer / 60 : 1;

        // Pulsing and sparkling effect
        let pulseScale = 1 + Math.sin(gameState.frameCount * 0.2) * 0.1;
        let sparkleOffset = Math.sin(gameState.frameCount * 0.15) * 10;

        ctx.save();
        ctx.translate(0, sparkleOffset);
        ctx.scale(pulseScale, pulseScale);

        // Background box for message
        ctx.globalAlpha = alpha * 0.8;
        ctx.fillStyle = 'rgba(255, 215, 0, 1)'; // Gold background
        ctx.strokeStyle = 'rgba(255, 255, 255, 1)';
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.roundRect(-200, -50, 400, 100, 20);
        ctx.fill();
        ctx.stroke();

        // Main message text
        ctx.globalAlpha = alpha;
        ctx.fillStyle = '#8a2be2'; // Purple text
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.font = 'bold 24px Arial';
        ctx.fillText('ðŸ† BROWNIE POINT! ðŸ†', 0, -20);

        ctx.font = '20px Arial';
        ctx.fillStyle = '#ff1493'; // Deep pink
        ctx.fillText('â­ MoonDustBeamcharger â­', 0, 10);

        ctx.font = '16px Arial';
        ctx.fillStyle = '#00bfff'; // Sky blue
        ctx.fillText('âœ¨ moondustBeamcharger âœ¨', 0, 35);

        ctx.restore();

        // Floating sparkle effects around the message
        for (let i = 0; i < 8; i++) {
          let angle = (gameState.frameCount * 0.05 + i * Math.PI / 4);
          let x = Math.cos(angle) * (80 + Math.sin(gameState.frameCount * 0.1) * 20);
          let y = Math.sin(angle) * (40 + Math.cos(gameState.frameCount * 0.08) * 15);

          ctx.save();
          ctx.translate(x, y);
          ctx.rotate(gameState.frameCount * 0.1 + i);
          ctx.globalAlpha = alpha * 0.8;

          ctx.font = `${16 + Math.sin(gameState.frameCount * 0.2 + i) * 4}px Arial`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = '#ffffff';

          if (i % 2 === 0) {
            ctx.fillText('â­', 0, 0);
          } else {
            ctx.fillText('âœ¨', 0, 0);
          }
          ctx.restore();
        }

        if (brownieTimer <= 0) {
          setShowBrownie(false);
        }
      }

      // Display brownie points counter
      ctx.fillStyle = 'rgba(255, 215, 0, 0.8)'; // Gold background
      ctx.strokeStyle = 'rgba(255, 255, 255, 1)';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.roundRect(-canvas.width / 2 + 20, -canvas.height / 2 + 55, 250, 25, 8);
      ctx.fill();
      ctx.stroke();

      ctx.fillStyle = '#8a2be2'; // Purple text
      ctx.font = 'bold 14px Arial';
      ctx.fillText(`ðŸ† Brownie Points: ${browniePoints}`, -canvas.width / 2 + 25, -canvas.height / 2 + 112);
      ctx.restore();

      ctx.restore();

      animationId = requestAnimationFrame(animate);
    };

    animationId = requestAnimationFrame(animate);

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [score, browniePoints, showBrownie, brownieTimer]);

  return (
    <div style={{ margin: 0, overflow: 'hidden', background: 'linear-gradient(135deg, #1a1a3a 0%, #0f0f1f 100%)' }}>
      {/* Sparkly title */}
      <div
        style={{
          position: 'fixed',
          top: 20,
          left: '50%',
          transform: 'translateX(-50%)',
          zIndex: 99999,
          fontFamily: 'Arial, sans-serif',
          fontSize: '2.5em',
          fontWeight: 'bold',
          textAlign: 'center',
          color: '#fff',
          background: 'rgba(0, 0, 0, 0.3)',
          padding: '10px 20px',
          borderRadius: 15,
          textShadow: '0 0 10px #ff69b4, 0 0 20px #ff1493, 0 0 30px #ff69b4, 0 0 40px #ff1493, 0 0 50px #ff69b4, 0 0 60px #ff1493',
          animation: 'sparkle 2s ease-in-out infinite alternate, glow 3s ease-in-out infinite',
          letterSpacing: 3,
          pointerEvents: 'none',
          border: '2px solid rgba(255, 105, 180, 0.5)',
        }}
      >
        ðŸ§š Moon Dust OdysseyðŸ§š
      </div>

      <canvas
        ref={canvasRef}
        style={{
          display: 'block',
          margin: '0 auto',
          position: 'relative',
          zIndex: 1,
          cursor: 'pointer'
        }}
      />
    </div>
  );
