import React, { useState, useEffect, useRef } from 'react';

const MoonJumperGame = () => {
    const canvasRef = useRef(null);
    const [score, setScore] = useState(0);
    const [gameStarted, setGameStarted] = useState(false);
    const gameRef = useRef({
        cat: { 
            angle: 0,           // Angle on moon surface
            distance: 200,      // Distance from moon center  
            x: 0, y: 0,         // Cartesian coordinates
            vx: 0, vy: 0,       // Velocity when jumping
            r: 30,              // Radius
            isOnMoon: true      // Whether cat is on moon surface
        },
        platforms: [],
        stars: [],
        moonRotation: 0,
        gravity: 0.4,
        jumpForce: 8,
        animationId: null
    });

    // Separate effect for initializing the game (runs once)
    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');

        // Set canvas size
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;

        // Initialize game objects
        const game = gameRef.current;

        // Create platforms
        game.platforms = [];
        for (let i = 0; i < 8; i++) {
            game.platforms.push({
                angle: i * (Math.PI / 4),
                distance: 200
            });
        }

        // Create stars
        game.stars = [];
        for (let i = 0; i < 50; i++) {
            game.stars.push({
                x: (Math.random() - 0.5) * canvas.width,
                y: (Math.random() - 0.5) * canvas.height,
                size: Math.random() * 3 + 1,
                twinkle: Math.random() * Math.PI * 2,
                speed: Math.random() * 0.02 + 0.01
            });
        }

        // Initialize cat position - start on the first platform
        const firstPlatformAngle = 0;
        game.cat.x = 200 * Math.cos(firstPlatformAngle);
        game.cat.y = 200 * Math.sin(firstPlatformAngle) - game.cat.r / 2;
        game.cat.vy = 0;
        game.cat.vx = 0;
        game.cat.currentPlatform = 0; // Track which platform the cat is on
        game.cat.isJumping = false;

        setGameStarted(true);
    }, []);

    // Separate effect for game loop (runs when game starts)
    useEffect(() => {
        if (!gameStarted) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        const game = gameRef.current;


        // Game loop
        function gameLoop() {
            // Clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw gradient background
            const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
            gradient.addColorStop(0, 'rgb(25, 25, 60)');
            gradient.addColorStop(1, 'rgb(10, 10, 25)');
            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            // Center coordinate system
            ctx.save();
            ctx.translate(canvas.width / 2, canvas.height / 2);

            // Draw twinkling stars
            game.stars.forEach(star => {
                star.twinkle += star.speed;
                const alpha = (Math.sin(star.twinkle) + 1) / 2 * 0.8 + 0.2;
                const size = star.size * (1 + Math.sin(star.twinkle * 2) * 0.3);

                ctx.save();
                ctx.globalAlpha = alpha;
                ctx.shadowColor = `rgba(255, 255, 200, ${alpha * 0.8})`;
                ctx.shadowBlur = size * 3;

                ctx.font = `${size * 8}px Arial`;
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText('⭐', star.x, star.y);

                ctx.restore();
                ctx.shadowBlur = 0;
            });

            // Draw animated moon
            const moonScale = 1 + Math.sin(Date.now() * 0.001) * 0.1;
            const moonGlow = (Math.sin(Date.now() * 0.002) + 1) / 2 * 0.5 + 0.5;

            ctx.save();
            ctx.scale(moonScale, moonScale);
            ctx.shadowColor = `rgba(255, 255, 150, ${moonGlow})`;
            ctx.shadowBlur = 30;
            ctx.fillStyle = '#FFF8DC';
            ctx.font = '60px Arial';
            ctx.textAlign = 'center';
            ctx.fillText('🌙', 0, 0);
            ctx.restore();

            // Draw rotating platforms
            ctx.save();
            ctx.rotate(game.moonRotation);

            game.platforms.forEach(platform => {
                const x = platform.distance * Math.cos(platform.angle);
                const y = platform.distance * Math.sin(platform.angle);

                ctx.fillStyle = 'rgba(200, 200, 255, 0.8)';
                ctx.strokeStyle = 'rgba(255, 255, 255, 0.6)';
                ctx.lineWidth = 2;

                ctx.beginPath();
                ctx.ellipse(x, y, 20, 5, 0, 0, Math.PI * 2);
                ctx.fill();
                ctx.stroke();
            });

            ctx.restore();

            // Update and draw cat
            const cat = game.cat;

            if (!cat.isJumping) {
                // Cat follows the current platform when not jumping
                const currentPlatform = game.platforms[cat.currentPlatform];
                const platformAngle = currentPlatform.angle + game.moonRotation;
                cat.x = currentPlatform.distance * Math.cos(platformAngle);
                cat.y = currentPlatform.distance * Math.sin(platformAngle) - cat.r / 2;
            } else {
                // Cat is in the air - apply physics
                cat.vy += game.gravity;
                cat.y += cat.vy;
                cat.x += cat.vx;

                // Check for landing on any platform
                let landed = false;
                for (let i = 0; i < game.platforms.length; i++) {
                    const platform = game.platforms[i];
                    const px = platform.distance * Math.cos(platform.angle + game.moonRotation);
                    const py = platform.distance * Math.sin(platform.angle + game.moonRotation);
                    const dist = Math.sqrt((cat.x - px) ** 2 + ((cat.y + cat.r / 2) - py) ** 2);

                    if (dist < cat.r * 1.2 && cat.vy >= 0) {
                        // Cat landed on platform
                        cat.isJumping = false;
                        cat.vy = 0;
                        cat.vx = 0;
                        cat.currentPlatform = i;
                        landed = true;
                        break;
                    }
                }

                // Check if cat fell
                if (cat.y > canvas.height / 2) {
                    // Reset cat to first platform
                    cat.currentPlatform = 0;
                    cat.isJumping = false;
                    cat.vy = 0;
                    cat.vx = 0;
                    setScore(0);
                }
            }

            // Draw cat
            ctx.fillStyle = '#38BDF8';
            ctx.beginPath();
            ctx.arc(cat.x, cat.y, cat.r / 2, 0, Math.PI * 2);
            ctx.fill();

            // Cat eyes
            ctx.fillStyle = 'black';
            ctx.beginPath();
            ctx.arc(cat.x - 5, cat.y - 5, 2, 0, Math.PI * 2);
            ctx.fill();
            ctx.beginPath();
            ctx.arc(cat.x + 5, cat.y - 5, 2, 0, Math.PI * 2);
            ctx.fill();

            // Update rotation
            game.moonRotation += 0.002 + score * 0.0001;

            ctx.restore();

            game.animationId = requestAnimationFrame(gameLoop);
        }

        // Check if cat is on platform
        function isOnPlatform() {
            const cat = game.cat;

            for (let platform of game.platforms) {
                const px = platform.distance * Math.cos(platform.angle + game.moonRotation);
                const py = platform.distance * Math.sin(platform.angle + game.moonRotation);
                const dist = Math.sqrt((cat.x - px) ** 2 + ((cat.y + cat.r / 2) - py) ** 2);

                if (dist < cat.r && cat.vy >= 0) {
                    return true;
                }
            }
            return false;
        }

        // Jump function
        function jump() {
            if (isOnPlatform()) {
                game.cat.vy = game.jumpForce;
                setScore(prev => prev + 1);
            }
        }

        // Start game
        gameLoop();

        // Cleanup
        return () => {
            if (game.animationId) {
                cancelAnimationFrame(game.animationId);
            }
        };
    }, [gameStarted, score]);

    // Separate effect for event handling
    useEffect(() => {
        if (!gameStarted) return;

        const game = gameRef.current;

        // Jump function
        function jump() {
            const cat = game.cat;

            if (!cat.isJumping) {
                // Calculate jump trajectory to next platform
                const nextPlatformIndex = (cat.currentPlatform + 1) % game.platforms.length;
                const nextPlatform = game.platforms[nextPlatformIndex];
                const nextAngle = nextPlatform.angle + game.moonRotation + 0.5; // Predict where platform will be

                const targetX = nextPlatform.distance * Math.cos(nextAngle);
                const targetY = nextPlatform.distance * Math.sin(nextAngle);

                // Calculate horizontal velocity needed
                const deltaX = targetX - cat.x;
                const deltaY = targetY - cat.y;

                // Set jumping state
                cat.isJumping = true;
                cat.vy = game.jumpForce;

                // Add horizontal movement towards next platform
                const jumpTime = Math.abs(2 * game.jumpForce / game.gravity);
                cat.vx = deltaX / jumpTime * 0.7; // 0.7 factor for realistic arc

                setScore(prev => prev + 1);
            }
        }

        // Event listeners
        function handleKeyDown(e) {
            if (e.code === 'Space' || e.code === 'ArrowUp') {
                e.preventDefault();
                jump();
            }
        }

        function handleClick(e) {
            e.preventDefault();
            jump();
        }

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('click', handleClick);

        // Cleanup
        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            window.removeEventListener('click', handleClick);
        };
    }, [gameStarted]);

    const gameContainerStyle = {
        position: 'relative',
        width: '100vw',
        height: '100vh',
        background: 'linear-gradient(135deg, #1a1a3a 0%, #0f0f1f 100%)',
        overflow: 'hidden',
        fontFamily: 'Arial, sans-serif'
    };

    const canvasStyle = {
        display: 'block',
        cursor: 'pointer'
    };

    const scoreStyle = {
        position: 'absolute',
        top: '20px',
        left: '20px',
        color: '#fff8aa',
        fontSize: '24px',
        fontWeight: 'bold',
        zIndex: 10
    };

    const instructionsStyle = {
        position: 'absolute',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        color: 'white',
        textAlign: 'center',
        fontSize: '16px'
    };

    return (
        <div style={gameContainerStyle}>
            <canvas ref={canvasRef} style={canvasStyle} />
            <div style={scoreStyle}>Score: {score}</div>
            <div style={instructionsStyle}>
                Jump to the next platform as the moon rotates! Press SPACEBAR, ↑ or CLICK to jump!
            </div>
        </div>
    );
};

export default MoonJumperGame;
